import { registerNode } from 'three/src/nodes/core/Node.js';
import TempNode from 'three/src/nodes/core/TempNode.js';
import { cameraProjectionMatrix } from 'three/src/nodes/accessors/Camera.js';
import { modelViewMatrix } from 'three/src/nodes/accessors/ModelNode.js';
import { positionLocal } from 'three/src/nodes/accessors/Position.js';
import { nodeProxy, varying } from 'three/src/nodes/tsl/TSLBase.js';

class ModelViewProjectionNode extends TempNode {

	constructor( positionNode = null ) {

		super( 'vec4' );

		this.positionNode = positionNode;

	}

	setup( builder ) {

		if ( builder.shaderStage === 'fragment' ) {

			return varying( builder.context.mvp );

		}

		const position = this.positionNode || positionLocal;

		return cameraProjectionMatrix.mul( modelViewMatrix ).mul( position );

	}

}

export default ModelViewProjectionNode;

ModelViewProjectionNode.type = /*@__PURE__*/ registerNode( 'ModelViewProjection', ModelViewProjectionNode );

export const modelViewProjection = /*@__PURE__*/ nodeProxy( ModelViewProjectionNode );
