import { registerNode } from 'three/src/nodes/core/Node.js';
import ViewportTextureNode from 'three/src/nodes/display/ViewportTextureNode.js';
import { nodeProxy } from 'three/src/nodes/tsl/TSLBase.js';
import { viewportUV } from 'three/src/nodes/display/ViewportNode.js';

import { DepthTexture } from 'three/src/textures/DepthTexture.js';

let sharedDepthbuffer = null;

class ViewportDepthTextureNode extends ViewportTextureNode {

	constructor( uvNode = viewportUV, levelNode = null ) {

		if ( sharedDepthbuffer === null ) {

			sharedDepthbuffer = new DepthTexture();

		}

		super( uvNode, levelNode, sharedDepthbuffer );

	}

}

export default ViewportDepthTextureNode;

ViewportDepthTextureNode.type = /*@__PURE__*/ registerNode( 'ViewportDepthTexture', ViewportDepthTextureNode );

export const viewportDepthTexture = /*@__PURE__*/ nodeProxy( ViewportDepthTextureNode );
