ViewportSharedTextureNode.type = /*@__PURE__*/ registerNode( 'ViewportSharedTexture', ViewportSharedTextureNode );

export const viewportSharedTexture = /*@__PURE__*/ nodeProxy( ViewportSharedTextureNode );
