import { registerNode } from 'three/src/nodes/core/Node.js';
import FogNode from 'three/src/nodes/fog/FogNode.js';
import { nodeProxy } from 'three/src/nodes/tsl/TSLBase.js';

class FogExp2Node extends FogNode {

	constructor( colorNode, densityNode ) {

		super( colorNode );

		this.isFogExp2Node = true;

		this.densityNode = densityNode;

	}

	setup( builder ) {

		const viewZ = this.getViewZNode( builder );
		const density = this.densityNode;

		return density.mul( density, viewZ, viewZ ).negate().exp().oneMinus();

	}

}

export default FogExp2Node;

FogExp2Node.type = /*@__PURE__*/ registerNode( 'FogExp2', FogExp2Node );

export const densityFog = /*@__PURE__*/ nodeProxy( FogExp2Node );
