import { registerNode } from 'three/src/nodes/core/Node.js';
import AnalyticLightNode from 'three/src/nodes/lighting/AnalyticLightNode.js';
import { getDistanceAttenuation } from 'three/src/nodes/lighting/LightUtils.js';
import { uniform } from 'three/src/nodes/core/UniformNode.js';
import { objectViewPosition } from 'three/src/nodes/accessors/Object3DNode.js';
import { positionView } from 'three/src/nodes/accessors/Position.js';

class PointLightNode extends AnalyticLightNode {

	constructor( light = null ) {

		super( light );

		this.cutoffDistanceNode = uniform( 0 );
		this.decayExponentNode = uniform( 0 );

	}

	update( frame ) {

		const { light } = this;

		super.update( frame );

		this.cutoffDistanceNode.value = light.distance;
		this.decayExponentNode.value = light.decay;

	}

	setup( builder ) {

		const { colorNode, cutoffDistanceNode, decayExponentNode, light } = this;

		const lightingModel = builder.context.lightingModel;

		const lVector = objectViewPosition( light ).sub( positionView ); // @TODO: Add it into LightNode

		const lightDirection = lVector.normalize();
		const lightDistance = lVector.length();

		const lightAttenuation = getDistanceAttenuation( {
			lightDistance,
			cutoffDistance: cutoffDistanceNode,
			decayExponent: decayExponentNode
		} );

		const lightColor = colorNode.mul( lightAttenuation );

		const reflectedLight = builder.context.reflectedLight;

		lightingModel.direct( {
			lightDirection,
			lightColor,
			reflectedLight
		}, builder.stack, builder );

	}

}

export default PointLightNode;

PointLightNode.type = /*@__PURE__*/ registerNode( 'PointLight', PointLightNode );
